sub getmat{
	$_ = shift();
	s/(\s*;\s*)+$//g;
	s/^.*\[//g;
	s/^/[/;
	s/(\d)\s+(\d)/\1,\2/g;
	s/(\d)\s+(\d)/\1,\2/g;
	s/;/],[/g;
	return $_;
}

#create your adjacency matrix, nodes do whatever else you need.
my @admat = (
	eval(getmat($adj))
);

@nodes = map{ $_+1 } (0..$#admat);

#create a new graph
my $graph = GraphViz->new( 
	layout => 'circo',
	directed => 0);

#add all your nodes to the graph
foreach (@nodes) {
    $graph->add_node($_);
}

#create edges as defined by your adjacecncy matrix
for($i=0; $i<@nodes; $i++){
    for($j=$i+1; $j<@nodes; $j++){
        if($admat[$i][$j] eq 1){
            print "adding edge from $i to $j\n";
			#$graph->add_edge($nodes[$i] => $nodes[$j], color => 'gray');
		}
    }
}


print "Create paths...\n";
drawpath('red',  qw( 50 47 45 43 49 42 46 44 48 35 40 41 37 32 39 34 36 38 28 33 31 30 26 18 25 20 29 27 23 24 22 16 19 17 21 11 10 14 12 15 7 13 9 8 4 2 6 1 3 5));

sub drawpath{
	$color = shift;
	print "  drawing $color path...\n";
	reduce{ 
		if($admat[$a-1][$b-1] eq 1){
			$graph->add_edge($a => $b, color => $color, arrowhead => 'normal'); 
		}else{
			print "    jumping from ".($a)." to ".($b)."\n";
		};
		$b
	} @_;
};


#render the graph to a png-file
$graph->as_png("pretty.png");
